CREATE TABLE device_binding (  deviceID   VARCHAR(37) NOT NULL  COMMENT 'ID主键',  deviceName VARCHAR(64) NOT NULL  COMMENT '设备名称',  mac        VARCHAR(37) NOT NULL  COMMENT 'MAC地址',  addDate    DATETIME    NOT NULL  COMMENT '添加时间',  PRIMARY KEY (deviceID));CREATE TABLE wifi_log (  id          VARCHAR(37) NOT NULL  COMMENT 'ID主键',  idshop      VARCHAR(20) NOT NULL  COMMENT '店铺ID',  idwifi      VARCHAR(20) NOT NULL  COMMENT 'wifi ID',  idvisit     VARCHAR(37) NOT NULL  COMMENT '访问ID',  mac_device  VARCHAR(48) NOT NULL  COMMENT '手机Mac',  time_local  DATETIME    NOT NULL  COMMENT '本地时间',  create_time DATETIME    NOT NULL  COMMENT '服务器时间',  update_time DATETIME    NOT NULL  COMMENT '更新时间',  PRIMARY KEY (id),  INDEX fk_t_mac_log_t_shop_1 (idshop),  INDEX fk_t_mac_log_t_wifi_1 (idwifi),  INDEX fk_t_mac_log_t_visit_1 (idvisit),  INDEX create_time (create_time));CREATE TABLE login (  id             VARCHAR(37) NOT NULL  COMMENT 'ID主键',  idshop         VARCHAR(20) NOT NULL  COMMENT '店铺ID',  idwifi         VARCHAR(20) NOT NULL  COMMENT 'wifi ID',  appid          VARCHAR(37) NOT NULL  COMMENT '公众号ID',  openid         VARCHAR(37) NOT NULL  COMMENT '用户开放ID',  wxshopid       VARCHAR(37) NOT NULL  COMMENT '微信店面ID',  auth_type      INT(11)     NOT NULL  COMMENT '认证类型',  mac_device     VARCHAR(48) NOT NULL  COMMENT '设备Mac',  time_from_last INT(11)     NOT NULL  COMMENT '上次登录到本次的时间（秒）',  is_new_user    TINYINT(1)  NOT NULL  COMMENT '是否新用户',  end_brand      VARCHAR(16) NOT NULL  COMMENT '设备品牌',  time_local     DATETIME    NOT NULL  COMMENT '本地时间',  create_time    DATETIME    NOT NULL  COMMENT '服务器时间',  update_time    DATETIME    NOT NULL  COMMENT '更新时间',  PRIMARY KEY (id),  INDEX fk_t_customer_t_shop_1 (idshop),  INDEX fk_t_customer_t_wifi_1 (idwifi),  INDEX time_from_last (time_from_last),  INDEX create_time (create_time));CREATE TABLE visit (  id             VARCHAR(37) NOT NULL  COMMENT 'ID主键',  idshop         VARCHAR(20) NOT NULL  COMMENT '店铺ID',  idwifi         VARCHAR(20) NOT NULL  COMMENT 'wifi ID',  mac_device     VARCHAR(48) NOT NULL  COMMENT '设备Mac',  time_entry     DATETIME    NOT NULL  COMMENT '进入时间',  time_leave     DATETIME    NOT NULL  COMMENT '离开时间',  time_duration  INT(11)     NOT NULL  COMMENT '驻店时间',  time_from_last INT(11)     NOT NULL  COMMENT '上次到本次的时间（秒）',  is_new_user    TINYINT(1)  NOT NULL  COMMENT '是否新用户',  count_logs     INT(11)     NOT NULL  COMMENT '日志条数',  end_brand      VARCHAR(16) NOT NULL  COMMENT '设备品牌',  create_time    DATETIME    NOT NULL  COMMENT '服务器时间',  update_time    DATETIME    NOT NULL  COMMENT '更新时间',  PRIMARY KEY (id),  INDEX fk_t_customer_t_shop_1 (idshop),  INDEX fk_t_customer_t_wifi_1 (idwifi),  INDEX time_leave (time_leave),  INDEX time_duration (time_duration),  INDEX time_from_last (time_from_last),  INDEX create_time (create_time));CREATE TABLE shop (  shopID                              VARCHAR(37)  NOT NULL  COMMENT 'ID主键',  shopName                            VARCHAR(32)  NOT NULL  COMMENT '名称',  config_wifi_visit_expired           INT(11)      NOT NULL  COMMENT '访问过期时间段（60分钟）',  config_wifi_user_expired            INT(11)      NOT NULL  COMMENT '用户过期时间段（365天）',  config_wifi_api_visit_counts        VARCHAR(64)  NOT NULL  COMMENT '到访次数统计规则（1,2,5）次数',  config_wifi_api_visit_duration      VARCHAR(64)  NOT NULL  COMMENT '到访时长统计规则（5,30,60,120）分钟',  config_wifi_api_visit_duration_deep FLOAT(11, 0) NOT NULL  COMMENT '到访时长达到深访的标准（40分钟）',  config_wifi_api_visit_duration_jump FLOAT(11, 0) NOT NULL  COMMENT '到访时长达到跳出的标准（10分钟）',  config_wifi_api_visit_period        VARCHAR(64)  NOT NULL  COMMENT '到访周期统计规则（1,2,4,7,14）天',  config_wifi_api_liveness            VARCHAR(64)  NOT NULL  COMMENT '活跃度统计(1,7,15,30天)',  createTime                          DATETIME     NOT NULL  COMMENT '服务器时间',  PRIMARY KEY (shopID));